// 声明一个数组并初始化
const myArray = [1, 2, 3, 4, 5]
// 声明一个对象并初始化
const myObject = {
  name: 'Alice',
  age: 30,
  city: 'New York'
}
// 输出原始数组和对象
console.log('The original array is:', myArray)
console.log('The original object is:', myObject)
// 向数组中添加一个元素
myArray.push(6)
// 更新对象中的一个属性
myObject.age = 31
// 输出修改后的数组和对象
console.log('The modified array is:', myArray)
console.log('The modified object is:', myObject)
